import PropTypes from 'prop-types'
import React, { Component } from 'react'

import './askWord.css'


class askWord extends Component {
  state = { word :''}

  handleWordUpdate = event => {
    this.setState({word : event.target.value.toUpperCase() })
  }

  persistWord = event => {
    event.preventDefault()
    const newWord = {word : this.state.word}
    this.wordEntry
  }

  wordEntry (entry, onStored){
    onStored(entry)
  }

  render() {
    return (
      <form className="askWord" onSubmit = {this.persistWord}>
        <p>
          <label>
            Choisissez un mot :
            <input 
            type="text" 
            onChange = {this.handleWordUpdate}
            value = {this.state.word}
            maxLength = {14}
            />

          </label>
          <button type="submit">C'est parti !</button>
        </p>
      </form>
    )
  }
}

askWord.propTypes = {
  word: PropTypes.number.isRequired,
  propStored : PropTypes.func.isRequired,
}

export default askWord
