import PropTypes from 'prop-types'
import React, { Component } from 'react'

import './AskWord.css'

import { saveHOFEntry } from './HallOfFame'

class AskWord extends Component {
  state = { temporaryWord :''}

  handleWinnerUpdate = event => {
    this.setState({temporaryWord : event.target.value.toUpperCase() })
  }

  persistWinner = event => {
    event.preventDefault()
    const newEntry = {guesses: this.props.guesses, player : this.state.winner}
    saveHOFEntry(newEntry, this.props.onStored)
  }


  render() {
    return (
      <form className="highScoreInput" onSubmit = {this.persistWinner}>
        <p>
          <label>
            Bravo ! Entre ton prénom :
            <input 
            type="text" 
            autoComplete="given-name" 
            onChange = {this.handleWinnerUpdate}
            value = {this.state.winner}/>
          </label>
          <button type="submit">J’ai gagné !</button>
        </p>
      </form>
    )
  }
}

AskWord.propTypes = {
  guesses: PropTypes.number.isRequired,
  propStored : PropTypes.func.isRequired,
}

export default AskWord
